import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Async thunk to fetch bookings
export const fetchAmenityBookings = createAsyncThunk(
    'amenityBooking/fetchAmenityBookings',
    async ({ id, userId }) => {
        const response = await axios.get(`http://192.168.29.226:2000/api/getAmenityByIdAndUserId/${id}/${userId}`);
        return response.data;
    }
);

// // Async thunk to create a new booking
