//   async (bookingData) => {
//     const response = await axios.post('/api/amenities/bookings', bookingData); // Replace with your API endpoint
//     return response.data;
//   }
// );

const amenityBookingSlice = createSlice({
    name: 'amenityBooking',
    initialState: {
        bookings: [],
        loading: false,
        error: null,
    },
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(fetchAmenityBookings.pending, (state) => {
                state.loading = true;
                state.error = null;
            })
            .addCase(fetchAmenityBookings.fulfilled, (state, action) => {
                state.loading = false;
                console.log(action.payload)
                state.bookings = action.payload;
            })
            .addCase(fetchAmenityBookings.rejected, (state, action) => {
                state.loading = false;
                state.error = action.error.message;
            })
        //   .addCase(createAmenityBooking.pending, (state) => {
        //     state.loading = true;
        //     state.error = null;
        //   })
        //   .addCase(createAmenityBooking.fulfilled, (state, action) => {
        //     state.loading = false;
        //     state.bookings.push(action.payload); // Add new booking to state
        //   })
        //   .addCase(createAmenityBooking.rejected, (state, action) => {
        //     state.loading = false;
        //     state.error = action.error.message;
        //   });
    },
});

export default amenityBookingSlice.reducer;
