const Amenities = () => {
  const layout = useWindowDimensions();
  const [societyId, setSocietyId] = useState('')
  const [date, setDate] = useState(new Date());
  const [show, setShow] = useState(false);
  const dispatch = useDispatch()
  const { amenities, status } = useSelector((state) => state.amenities)
  useEffect(() => {
    const getUserName = async () => {
      try {
        const userString = await AsyncStorage.getItem("user");
        if (userString !== null) {
          const user = JSON.parse(userString);
          setSocietyId(user.societyId);
        }
      } catch (error) {
        console.error("Failed to fetch the user from async storage", error);
      }
    };

    getUserName();
  }, []);
  useEffect(() => {
    if (societyId) {
      dispatch(getAmenitiesBySocietyId(societyId))
    }
  }, [societyId])
  const onChange = (event, selectedDate) => {
    const currentDate = selectedDate || date;
    setShow(Platform.OS === "ios");
    setDate(currentDate);
  };

  const showDatePicker = () => {
    setShow(true);
  };

  const [index, setIndex] = React.useState(0);
  const [routes] = React.useState([
    { key: "community", title: "Hall" },
    { key: "playArea", title: "Play area" },
    { key: "gym", title: "Gym" },
  ]);

  const renderScene = ({ route }) => {
    switch (route.key) {
      case "community":
        return <CommunityHall data={amenities} />;
      case "playArea":
        return (
          <PlayArea
            date={date}
            show={show}
            showDatePicker={showDatePicker}
            onChange={onChange}
            data={amenities}
          />
        );
      case "gym":
        return <Gym data={amenities} />;
      default:
        return null;
    }
  };

  const renderTabBar = (props) => (
    <TabBar
      {...props}
      indicatorStyle={styles.indicator}
      style={styles.tabBar}
      labelStyle={styles.labelStyle}
      activeColor="#192c4c"
      inactiveColor="#777"
    />
  );

  return (
    <TabView
      navigationState={{ index, routes }}
      renderScene={renderScene}
      onIndexChange={setIndex}
      initialLayout={{ width: layout.width }}
      renderTabBar={renderTabBar}
    />
  );
};

