import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  Modal,
  Pressable,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";
import { useNavigation } from "@react-navigation/native";
import { Rating } from "react-native-ratings";
import { useDispatch, useSelector } from "react-redux";
import { fetchComplaints, updateComplaintResolution } from "../../Redux/Slice/GetHelpSlice/ComplaintsSlice";
import AsyncStorage from "@react-native-async-storage/async-storage";

const GetHelp = () => {
  const navigation = useNavigation();
  const dispatch = useDispatch();


  const { loading, error, complaints } = useSelector((state) => state.complaints);
  const [societyId, setSocietyId] = useState("");
  const [userId, setUserId] = useState("");

  const [showTypeDropdown, setShowTypeDropdown] = useState(false);
  const [selectedType, setSelectedType] = useState(null);
  const [showCateDropdown, setShowCateDropdown] = useState(false);
  const [selectedCate, setSelectedCate] = useState(null);
  const [showStatuDropdown, setShowStatuDropdown] = useState(false);
  const [selectedStatu, setSelectedStatu] = useState(null);
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [rating, setRating] = useState(0);
  const [currentTicketId, setCurrentTicketId] = useState(null);
  const Types = ["All", "Society", "Personal"];
  const Cates = ["All", "Electrical", "Plumbing", "Lift", "Common Area", "Payment", "Car Parking", "Water", "Others"];
  const Status = ["All", "Resolved", "Pending"];



  useEffect(() => {
    const getUserName = async () => {
      try {
        const userString = await AsyncStorage.getItem("user");
        if (userString !== null) {
          const user = JSON.parse(userString);
          setSocietyId(user.societyId);
          setUserId(user.userId);
        }
      } catch (error) {
        console.error("Failed to fetch the user from async storage", error);
      }
    };
    getUserName();
  }, []);
  useEffect(() => {
    if (societyId) {
      dispatch(fetchComplaints(societyId));
    }
  }, [dispatch, societyId])

  const selectType = (Type) => {
    setSelectedType(Type);
    setShowTypeDropdown(false);
  };
  const selectCate = (Cate) => {
    setSelectedCate(Cate);
    setShowCateDropdown(false);
  };
  const selectStatu = (Statu) => {
    setSelectedStatu(Statu);
    setShowStatuDropdown(false);
  };
  const toggleDropdown = (dropdown) => {
    if (dropdown === "type") {
      setShowTypeDropdown(!showTypeDropdown);
      setShowCateDropdown(false);
      setShowStatuDropdown(false);
    } else if (dropdown === "cate") {
      setShowCateDropdown(!showCateDropdown);
      setShowTypeDropdown(false);
      setShowStatuDropdown(false);
    } else if (dropdown === "statu") {
      setShowStatuDropdown(!showStatuDropdown);
      setShowTypeDropdown(false);
      setShowCateDropdown(false);
    }
  };
  console.log(complaints)
  const handlebuttonPress = () => {
    navigation.navigate("Select the Category");
  };
  const getStatusColor = (status) => {
    switch (status) {
      case "Pending":
        return "orange";
      case "Resolved":
        return "green";
      default:
        return "gray";
    }
  };

  const filteredTickets = Array.isArray(complaints?.Complaints)
  ? complaints.Complaints.filter((ticket) => {
    const matchesType =
      selectedType && selectedType !== "All"
        ? ticket.complaintType === selectedType
        : true;

    const matchesCategory =
      selectedCate && selectedCate !== "All"
        ? ticket.complaintCategory === selectedCate
        : true;

    const matchesStatus =
      selectedStatu && selectedStatu !== "All"
        ? ticket.resolution === selectedStatu
        : true;

    const matchesUser = 
      (ticket.complaintType === "Society") ||
      (ticket.complaintType === "Personal" && ticket.userId === userId);

    return matchesType && matchesCategory && matchesStatus && matchesUser;
  })
  : [];




  const markAsResolved = async (complaintId) => {
    try {
      const result = await dispatch(updateComplaintResolution({ societyId, complaintId, resolution: "Resolved" }));
      if (result.type === "complaints/updateComplaintResolution/fulfilled") {
        dispatch(fetchComplaints(societyId))
      }
    }
    catch (error) {
      console.error(error)
    }
  };

  return (
    <View style={styles.container}>
      <View style={[styles.rowContainer, styles.row1]}>
        <View style={styles.dropdownContainer}>
          <TouchableOpacity
            style={styles.dropdown}
            onPress={() => toggleDropdown("type")}
          >
            <Text style={styles.fontWeightBold}>{selectedType || "Type"}</Text>
            <Icon
              name={showTypeDropdown ? "angle-up" : "angle-down"}
              size={20}
              color="#000"
              style={{ marginLeft: 5 }}
            />
          </TouchableOpacity>
          {showTypeDropdown && (
            <View style={styles.dropdownList}>
              {Types.map((Type, index) => (
                <TouchableOpacity
                  key={index}
                  onPress={() => selectType(Type)}
                  style={styles.dropdownItem}
                >
                  <Text>{Type}</Text>
                </TouchableOpacity>
              ))}
            </View>
          )}
        </View>
        <View style={styles.dropdownContainer}>
          <TouchableOpacity
            style={styles.dropdown}
            onPress={() => toggleDropdown("cate")}
          >
            <Text style={styles.fontWeightBold}>
              {selectedCate || "Category"}
            </Text>
            <Icon
              name={showCateDropdown ? "angle-up" : "angle-down"}
              size={20}
              color="#000"
              style={{ marginLeft: 5 }}
            />
          </TouchableOpacity>
          {showCateDropdown && (
            <View style={styles.dropdownList}>
              {Cates.map((Cate, index) => (
                <TouchableOpacity
                  key={index}
                  onPress={() => selectCate(Cate)}
                  style={styles.dropdownItem}
                >
                  <Text>{Cate}</Text>
                </TouchableOpacity>
              ))}
            </View>
          )}
        </View>
        <View style={styles.dropdownContainer}>
          <TouchableOpacity
            style={styles.dropdown}
            onPress={() => toggleDropdown("statu")}
          >
            <Text style={styles.fontWeightBold}>
              {selectedStatu || "Status"}
            </Text>
            <Icon
              name={showStatuDropdown ? "angle-up" : "angle-down"}
              size={20}
              color="#000"
              style={{ marginLeft: 5 }}
            />
          </TouchableOpacity>
          {showStatuDropdown && (
            <View style={styles.dropdownList}>
              {Status.map((Statu, index) => (
                <TouchableOpacity
                  key={index}
                  onPress={() => selectStatu(Statu)}
                  style={styles.dropdownItem}
                >
                  <Text>{Statu}</Text>
                </TouchableOpacity>
              ))}
            </View>
          )}
        </View>
      </View>
      <FlatList
        data={filteredTickets}
        keyExtractor={(item) => item._id}
        renderItem={({ item }) => (
          <View style={styles.ticketContainer}>
            <View style={styles.header}>
              <Text style={styles.type}>
                {item.complaintType} - {item.complaintCategory}
              </Text>
              <Text
                style={[
                  styles.status,
                  { backgroundColor: getStatusColor(item.resolution) },
                ]}
              >
                {item.resolution}
              </Text>
            </View>
            <View >
              <Text style={styles.date}>{item.complaintTitle}</Text>
              <Text style={styles.reqId}>{item.description}</Text>
            </View>
            <View >
              <Text style={styles.date}>Complaint Date:{item.dateAndTime}</Text>
              <Text style={styles.reqId}>Complaint By: {item.complaintBy}</Text>
            </View>
            {item.resolution === "Pending" && (
              <TouchableOpacity
                style={styles.resolveButton}
                onPress={() => markAsResolved(item.complaintId)}
              >
                <Text style={styles.resolveButtonText}>Resolved</Text>
              </TouchableOpacity>
            )}
            {item.resolution === "Resolved" && (
              <Text style={styles.reqId}>Resolved Date: {item.updatedAt}</Text>

            )}
          </View>
        )}
      />
      <TouchableOpacity style={styles.addButton} onPress={handlebuttonPress}>
        <Icon name="plus" size={20} color="#fff" />
      </TouchableOpacity>
    </View>
  );
};

