import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  SafeAreaView,
  ScrollView,
  Image,
  Alert,
  Modal,
} from "react-native";
import { Avatar, TextInput } from "react-native-paper";
import { useDispatch, useSelector } from "react-redux";
import {
  createVisitor,
  resetState,
} from "../../User/Redux/Slice/Security_Panel/VisitorsSlice";
import { fetchSocietyById } from "../../User/Redux/Slice/Security_Panel/SocietyByIdSlice";
import MyDialog from "../DialogBox/DialogBox";
import * as ImagePicker from "expo-image-picker";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { AntDesign, Entypo, MaterialIcons } from "@expo/vector-icons";

const AddGuest = ({ route, navigation }) => {
  const [name, setName] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [phoneNumber, setPhoneNumber] = useState("");
  const [block, setBlock] = useState("");
  const [flatNo, setFlatNo] = useState("");
  const [showBuildingDropdown, setShowBuildingDropdown] = useState(false);
  const [showFlatNoDropdown, setShowFlatNoDropdown] = useState(false);
  const [showDialog, setShowDialog] = useState(false);
  const [loading, setLoading] = useState(false);
  const [nameError, setNameError] = useState("");
  const [phoneNumberError, setPhoneNumberError] = useState("");
  const [blockError, setBlockError] = useState("");
  const [flatNoError, setFlatNoError] = useState("");
  const error = useSelector((state) => state.visitor.error);
  const successMessage = useSelector((state) => state.visitor.successMessage);
  const [buildings, setBuildings] = useState([]);
  const [flatsForSelectedBlock, setFlatsForSelectedBlock] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [isImageModalVisible, setImageModalVisible] = useState(false);
  const dispatch = useDispatch();

  const [societyId, setSocietyId] = useState(null);
  useEffect(() => {
    const getSocietyId = async () => {
      try {
        const user = await AsyncStorage.getItem("user");
        const id = JSON.parse(user).societyId;
        if (id !== null) {
          setSocietyId(id);
        } else {
          console.error("No societyId found in AsyncStorage");
        }
      } catch (error) {
        console.error("Error fetching societyId from AsyncStorage:", error);
      }
    };
    getSocietyId();
  }, []);
  const { society } = useSelector((state) => state.societyById);
  console.log(successMessage);

  useEffect(() => {
    if (society) {
      setBuildings(society.blocks);
    }
  }, [society]);

  useEffect(() => {
    if (societyId) {
      dispatch(fetchSocietyById(societyId));
    }
  }, [dispatch, societyId]);

  const selectBuilding = (building) => {
    setBlock(building.blockName);
    setShowBuildingDropdown(false);
    const fetchedFlats = fetchFlatsForBlock(building.blockName);
    setFlatsForSelectedBlock(fetchedFlats);
  };

  const fetchFlatsForBlock = (block) => {
    const flats =
      buildings.find((item) => item.blockName === block)?.flats || [];
    return flats;
  };

  const selectFlatNo = (flatNo) => {
    setFlatNo(flatNo.flatNumber);
    setShowFlatNoDropdown(false);
  };

  const validateInputs = () => {
    let isValid = true;
    setNameError("");
    setPhoneNumberError("");
    setBlockError("");
    setFlatNoError("");

    if (!name) {
      setNameError("Name is required");
      isValid = false;
    }

    const phonePattern = /^[0-9]{10}$/;
    if (!phoneNumber || !phonePattern.test(phoneNumber)) {
      setPhoneNumberError("Valid phone number is required");
      isValid = false;
    }

    if (!block) {
      setBlockError("Block selection is required");
      isValid = false;
    }

    if (!flatNo) {
      setFlatNoError("Flat number is required");
      isValid = false;
    }
    return isValid;
  };

  const handleConfirm = async () => {
    console.log("handleConfirm clicked");
    if (validateInputs()) {
      setLoading(true);
      const status = "Waiting";
      const role = "Guest";
      const date = new Date().toISOString();
      const formData = new FormData();
      formData.append("name", name);
      formData.append("phoneNumber", phoneNumber);
      formData.append("societyId", societyId);
      formData.append("status", status);
      formData.append("role", role);
      formData.append("block", block);
      formData.append("flatNo", flatNo);
      formData.append("date", date);

      if (imageFile) {
        formData.append("pictures", {
          uri: imageFile.uri,
          name: imageFile.name,
          type: imageFile.type,
        });
      }
      try {
        const response = await dispatch(createVisitor(formData));
        if (response.meta.requestStatus === "fulfilled") {
          setName("");
          setPhoneNumber("");
          setBlock("");
          setFlatNo("");
          setImageFile(null);
          setImagePreview(null);
          setShowDialog(true);
          setTimeout(() => {
            setShowDialog(false);
            dispatch(resetState());
            navigation.navigate("InandOut1");
          }, 1000);
        } else {
          setShowDialog(true);
          setTimeout(() => {
            setShowDialog(false);
          }, 1000);
        }
      } catch (error) {
        setLoading(false);
      } finally {
        setLoading(false);
      }
    }
  };

  const pickImage = async () => {
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: false,
      quality: 1,
    });

    if (!result.canceled) {
      const uri = result.assets[0]?.uri;
      if (uri) {
        setImageFile({ uri, name: uri.split("/").pop(), type: "image/jpeg" });
        setImagePreview(uri);
        setShowModal(false);
      }
    }
  };

  const takePhoto = async () => {
    try {
      const result = await ImagePicker.launchCameraAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: false,
        quality: 1,
      });

      if (!result.canceled) {
        const uri = result.assets && result.assets[0]?.uri;
        if (uri) {
          setImageFile({ uri, name: uri.split("/").pop(), type: "image/jpeg" });
          setImagePreview(uri);
          setShowModal(false);
        }
      }
    } catch (error) {
      console.error("Error launching camera:", error);
    }
  };

  const deletePhoto = () => {
    Alert.alert(
      "Remove Profile Photo",
      "Are you sure you want to remove the profile photo?",
      [
        {
          text: "Cancel",
          style: "cancel",
        },
        {
          text: "Remove",
          onPress: () => {
            setImageFile(null);
            setImagePreview(null);
            setShowModal(false);
          },
          style: "destructive",
        },
      ],
      { cancelable: true }
    );
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const closeCross = () => {
    setShowModal(false);
  };

  const handleAvatarPress = () => {
    if (imagePreview) {
      setImageModalVisible(true);
    } else {
      Alert.alert("No Image", "Please upload an image.");
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollViewContent}>
        <View style={styles.avatarWrapper}>
          <TouchableOpacity
            style={styles.avatarContainer}
            onPress={handleAvatarPress}
          >
            {imagePreview ? (
              <Avatar.Image
                style={styles.avatar}
                resizeMode="cover"
                size={174}
                source={{ uri: imagePreview }}
              />
            ) : (
              <Avatar.Image
                size={174}
                style={styles.avatar}
                color="#fff"
                source={require("../../../assets/Security/images/user.png")}
              />
            )}
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.cameraIconContainer}
            onPress={handleOpenModal}
          >
            <MaterialCommunityIcons
              name="camera-outline"
              size={25}
              color="#ddd"
            />
          </TouchableOpacity>
        </View>

        <View style={styles.inputContent}>
          <TextInput
            style={[styles.inputBlock, nameError && { borderColor: "red" }]}
            label="Name *"
            value={name}
            mode="outlined"
            outlineColor={nameError ? "red" : "#ccc"}
            theme={{
              colors: {
                primary: nameError ? "red" : "#800336",
              },
            }}
            onChangeText={setName}
          />
          {nameError ? (
            <Text style={styles.errorMessage}>{nameError}</Text>
          ) : null}

          <TextInput
            style={[
              styles.inputBlock,
              { marginTop: 10 },
              phoneNumberError && { borderColor: "red" },
            ]}
            label="Phone Number *"
            value={phoneNumber}
            keyboardType="phone-pad"
            mode="outlined"
            outlineColor={phoneNumberError ? "red" : "#CCC"}
            theme={{
              colors: {
                primary: phoneNumberError ? "red" : "#800336",
              },
            }}
            onChangeText={setPhoneNumber}
          />
          {phoneNumberError ? (
            <Text style={styles.errorMessage}>{phoneNumberError}</Text>
          ) : null}

          <TouchableOpacity
            style={[
              styles.dropdownButton,
              showBuildingDropdown && styles.dropdownActive,
              { marginTop: 15 },
              blockError && { borderColor: "red" },
            ]}
            onPress={() => setShowBuildingDropdown(!showBuildingDropdown)}
          >
            <Text style={styles.dropdownButtonText}>
              {block ? `${block}` : "Select Block *"}
            </Text>
            <Text>
              <MaterialIcons
                name={
                  showBuildingDropdown ? "arrow-drop-up" : "arrow-drop-down"
                }
                size={20}
                color="#000"
                style={{ marginRight: 5 }}
              />
            </Text>
          </TouchableOpacity>
          {blockError ? (
            <Text style={styles.errorMessage}>{blockError}</Text>
          ) : null}

          {showBuildingDropdown && (
            <View style={styles.dropdownMenu}>
              {buildings.map((building) => (
                <TouchableOpacity
                  key={building.blockName}
                  style={styles.dropdownItem}
                  onPress={() => selectBuilding(building)}
                >
                  <Text style={styles.dropdownItemText}>
                    {building.blockName}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          )}

          <TouchableOpacity
            style={[
              styles.dropdownButton,
              showFlatNoDropdown && styles.dropdownActive,
              { marginTop: 15 },
              flatNoError && { borderColor: "red" },
            ]}
            onPress={() => setShowFlatNoDropdown(!showFlatNoDropdown)}
          >
            <Text style={styles.dropdownButtonText}>
              {flatNo ? `${flatNo}` : "Select Flat Number *"}
            </Text>
            <Text>
              <MaterialIcons
                name={showFlatNoDropdown ? "arrow-drop-up" : "arrow-drop-down"}
                size={20}
                color="#000"
                style={{ marginRight: 5 }}
              />
            </Text>
          </TouchableOpacity>
          {flatNoError ? (
            <Text style={styles.errorMessage}>{flatNoError}</Text>
          ) : null}

          {showFlatNoDropdown && (
            <View style={styles.dropdownMenu}>
              {flatsForSelectedBlock.map((flat) => (
                <TouchableOpacity
                  key={flat.flatNumber}
                  style={styles.dropdownItem}
                  onPress={() => selectFlatNo(flat)}
                >
                  <Text style={styles.dropdownItemText}>{flat.flatNumber}</Text>
                </TouchableOpacity>
              ))}
            </View>
          )}
        </View>

        <TouchableOpacity
          style={styles.confirmButton}
          onPress={handleConfirm}
          disabled={loading}
        >
          <Text style={styles.confirmButtonText}>Add</Text>
        </TouchableOpacity>
        </ScrollView>
        <MyDialog
          message={successMessage || error}
          showDialog={showDialog}
          onClose={() => setShowDialog(false)}
        />
        <Modal
          animationType="fade"
          transparent={true}
          visible={showModal}
          onRequestClose={() => setShowModal(false)}
        >
          <View style={styles.modalBackground}>
            <View style={styles.modalContainer}>
              <View style={styles.modalHeader}>
                <Text style={{ fontSize: 22, fontWeight: "700" }}>
                  Profile Photo
                </Text>
                <TouchableOpacity style={{ position: "absolute", right: 0 }}>
                  <Entypo
                    name="circle-with-cross"
                    size={27}
                    color="#800336"
                    onPress={closeCross}
                  />
                </TouchableOpacity>
              </View>
              <View style={styles.icons}>
                <View style={styles.camera}>
                  <TouchableOpacity onPress={takePhoto}>
                    <MaterialCommunityIcons
                      name="camera-outline"
                      size={27}
                      color="#800336"
                    />
                    <Text style={styles.camText}>Camera</Text>
                  </TouchableOpacity>
                </View>
                <View>
                  <TouchableOpacity onPress={pickImage}>
                    <MaterialCommunityIcons
                      name="view-gallery-outline"
                      size={27}
                      color="#800336"
                    />
                    <Text style={styles.camText}>Gallery</Text>
                  </TouchableOpacity>
                </View>
                <View>
                  <TouchableOpacity onPress={deletePhoto}>
                    <AntDesign name="delete" size={27} color="#800336" />
                    <Text style={styles.camText}>Delete</Text>
                  </TouchableOpacity>
                </View>
              </View>
            </View>
          </View>
        </Modal>

      <Modal
        visible={isImageModalVisible}
        transparent={true}
        animationType="fade"
        onRequestClose={() => setImageModalVisible(false)}
      >
        <View style={styles.fullScreenImageContainer}>
          <View style={styles.profileHeader}>
            <View style={{ flexDirection: "row", alignItems: "center" }}>
              <TouchableOpacity onPress={()=> setImageModalVisible(false)}>

                <AntDesign name="arrowleft" size={28} color="#fff" />
              </TouchableOpacity>
              <Text style={styles.profileText}>Profile Photo</Text>
            </View>
            <TouchableOpacity onPress={() => setShowModal(true)}>
              <MaterialIcons name="edit" size={28} color="#fff" />
            </TouchableOpacity>
          </View>

          <Image
            source={{ uri: imagePreview }}
            style={styles.fullScreenImage}
            resizeMode="cover"
          />
        </View>
      </Modal>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  scrollViewContent: {
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  avatarWrapper: {
    alignItems: "center",
    justifyContent: "center",
    position: "relative",
  },
  avatarContainer: {
    alignItems: "center",
    justifyContent: "center",
  },

  avatar: {
    backgroundColor: "#ccc",

  },
  cameraIconContainer: {
    bottom: 60,
    right: -60,
    backgroundColor: "#800336",
    borderRadius: 30,
    padding: 10,
  },
  inputContent: {
    marginBottom: 20,
  },
  inputBlock: {
    marginBottom: 2,
    backgroundColor: "#fff",
  },
  errorMessage: {
    color: "red",
    fontSize: 12,
  },
  dropdownButton: {
    backgroundColor: "#fff",
    borderWidth: 1,
    borderColor: "#ccc",
    padding: 15,
    borderRadius: 4,
    marginBottom: 2,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  dropdownButtonText: {
    color: "#192c4c",
  },
  dropdownMenu: {
    backgroundColor: "#fff",
    borderColor: "#ccc",
    borderWidth: 1,
    borderRadius: 4,
    marginTop: 13,
  },
  dropdownActive: {
    borderColor: "#800336",
    borderWidth: 2,
  },
  dropdownItem: {
    padding: 15,
  },
  dropdownItemText: {
    color: "#192c4c",
  },
  confirmButton: {
    backgroundColor: "#800336",
    padding: 12,
    borderRadius: 8,
    alignItems: "center",
    marginTop: 30,
    marginBottom: 3,
  },
  confirmButtonText: {
    color: "#fff",
    fontSize: 20,
    fontWeight: '600',
    textAlign: "center",
  },
  modalBackground: {
    backgroundColor: "rgba(0,0,0,0.4)",
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  modalContainer: {
    backgroundColor: "white",
    position: "absolute",
    bottom: 0,
    borderTopRightRadius: 20,
    borderTopLeftRadius: 20,
    height: "auto",
    width: "100%",
    paddingVertical: 15,
    paddingHorizontal: 15,
  },
  modalHeader: {
    paddingTop: 7,
    flexDirection: "row",
