    width: width * 0.11,
    height: height * 0.05,
    marginTop: height * 0.023,
    marginTop: width * 0.03
  },
  iconLabel: {
    marginTop: 5,
    color: "#222222",
    textAlign: "center",
    marginTop: width * 0.03,
    marginTop: height * 0.03,
    fontSize: 12,
  },

  modalBackground: {
    flex: 1,
    backgroundColor: "rgba(0, 0, 0, 0.7)",
    justifyContent: "center",
    alignItems: "center",
  },
  modalContent: {
    backgroundColor: "white",
    borderRadius: 20,
    padding: 20,
    width: width * 0.8,
    alignItems: "center",
  },
  closeIconContainer: {
    position: "absolute",
    top: 10,
    right: 10,
  },
  closeIcon: {
    fontSize: 24,
    color: "#555",
  },
  modalImage: {
    backgroundColor: "#F3E1D5",
    borderColor: "#C59358",
    marginBottom: 20,
  },
  input: {
    width: "100%",
    height: 40,
    borderBottomWidth: 1,
    borderBottomColor: "gray",
    marginBottom: 20,
    paddingLeft: 10,
  },
  button: {
    width: "100%",
    height: 50,
    backgroundColor: "#7D0431",
    borderRadius: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
    fontSize: 16,
  },
  entryCode: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#192c4c",
    marginBottom: 20,
  },
  qrImage: {
    width: 150,
    height: 150,
    marginBottom: 20,
  },
  shareButton: {
    width: "100%",
    height: 50,
    backgroundColor: "#7D0431",
    borderRadius: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  shareButtonContent: {
    flexDirection: "row",
