import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  StyleSheet,
  Image,
  ScrollView,
  TouchableOpacity,
  Modal,
  Alert,
  Share,
} from "react-native";
import { Ionicons } from "@expo/vector-icons";
import { Avatar, RadioButton } from "react-native-paper";
import { fetchServices } from "../../../Redux/Slice/ServiceSlice/ServiceSlice";
import { useDispatch, useSelector } from "react-redux";
import { Rating } from 'react-native-ratings';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { addService } from "../../../Redux/Slice/ServiceSlice/AddServiceSlice";

const PaperBoyProfile = ({ navigation, route }) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [secondModalVisible, setSecondModalVisible] = useState(false);
  const dispatch = useDispatch();
  const { id, userid } = route.params;

  const [radioValue, setRadioValue] = useState("Time of India");
  const { data, loading, error } = useSelector((state) => state.services);
  const [userId, setUserId] = useState(null);
  const [flatNumber, setFlatNumber] = useState(null);
  const [blockName, setblockName] = useState(null);
  const [societyId, setSocietyId] = useState(null);
  useEffect(() => {
    const getuser = async () => {
      try {
        const userString = await AsyncStorage.getItem('user');
        if (userString !== null) {
          const user = JSON.parse(userString);
          setUserId(user.userId);
          setblockName(user.buildingName);
          setFlatNumber(user.flatNumber);
          setSocietyId(user.societyId);
        } else {
          console.error('No societyId found in AsyncStorage');
        }
      } catch (error) {
        console.error('Error fetching societyId from AsyncStorage:', error);
      }
    };
    getuser();
  }, []);
  useEffect(() => {
    dispatch(fetchServices());
  }, [dispatch]);

  const handleAddPress = () => {
    if (radioValue) {
      // Proceed with adding to household
      setModalVisible(true);
    } else {
      Alert.alert("Please select a News Paper.");
    }
  };


  const handleContinuePress = async () => {
    try {
      const serviceData = {
        societyId: societyId,
        serviceType: "paperBoy",
        userid: userid,
        list: [
          {
            userId: userId,
            Block: blockName,
            flatNumber: flatNumber,
            newsPaper: radioValue
          },
        ],
      };
      const result = await dispatch(addService(serviceData));
      if (result.type === "services/addService/fulfilled") {
        setModalVisible(false);
        Alert.alert(result.payload.message);
      }
    } catch (error) {
      console.error(error)
    }
  };


  const filteredpaperBoy = data.paperBoy.find(driver => driver._id === id);

  if (!filteredpaperBoy) {
    return (
      <View style={styles.container}>
        <Text>Loading...</Text>
      </View>
    );
  }
  return (
    <>
      <ScrollView contentContainerStyle={styles.scrollViewContainer}>
        <View style={styles.container}>
          <View style={styles.card}>
            {/* <Image
              source={require("../../../../../assets/User/images/learner_5999070.png")}
              style={styles.image}
            /> */}
            <Avatar.Image source={{ uri: `http://192.168.29.226:2000${filteredpaperBoy.pictures}` }} size={100} style={styles.avatarContainer} />
            <View style={styles.content}>
              <Text style={styles.cardTitle}>Raju</Text>
              <View style={styles.locationContainer}>
                <Ionicons
                  name="call"
                  size={14}
                  color="#C59358"
                  style={styles.locationIcon}
                />
                <Text style={styles.cardSubText1}>{filteredpaperBoy.phoneNumber}</Text>
              </View>
              <View style={styles.locationContainer}>
                <Ionicons
                  name="location"
                  size={14}
                  color="#C59358"
                  style={styles.locationIcon}
                />
                <Text style={styles.cardSubText1}>
                  {filteredpaperBoy.address}
                </Text>
              </View>
              <View
                style={{ flexDirection: "row", justifyContent: "space-between" }}
              >
                <View style={styles.reviewContainer}>
                  <Rating
                    type='star'
                    ratingCount={5}
                    imageSize={20}
                    startingValue={filteredpaperBoy.list.rating}
                    readonly
                    ratingColor="#C59358"
                  />
                </View>
                <TouchableOpacity onPress={handleAddPress}>
                  <Text style={styles.newCardNumber1}>ADD</Text>
                </TouchableOpacity>
              </View>
            </View>
          </View>
          {/* New Card */}
          <View style={styles.newCard}>
            <View style={styles.newCardItem}>
              <Image
                source={require("../../../../../assets/User/images/punctuality.png")}
                style={styles.newCardImage}
              />
              <Text style={styles.newCardNumber}>4.75</Text>
              <Text style={styles.newCardText}>Time</Text>
              <Text style={styles.newCardSubText}>Punctual</Text>
            </View>
            <View style={styles.newCardItem}>
              <Image
                source={require("../../../../../assets/User/images/schedule.png")}
                style={styles.newCardImage}
              />
              <Text style={styles.newCardNumber}>4.5</Text>
              <Text style={styles.newCardText}>Quite</Text>
              <Text style={styles.newCardSubText}>Regular</Text>
            </View>
            <View style={styles.newCardItem}>
              <Image
                source={require("../../../../../assets/User/images/attitude.png")}
                style={styles.newCardImage}
              />
              <Text style={styles.newCardNumber}>4.9</Text>
              <Text style={styles.newCardText}>Good</Text>
              <Text style={styles.newCardSubText}>Behaviors</Text>
            </View>
          </View>

          <Text
            style={{
              fontSize: 18,
              fontWeight: "700",
              margin: 10,
              marginLeft: 20,
            }}
          >
            News Papers
          </Text>
          <View style={styles.radioButtonContainer}>
            <RadioButton.Group
              onValueChange={(newValue) => setRadioValue(newValue)}
              value={radioValue}
            >
              <View style={styles.radioButtonRow}>
                <RadioButton value="Time of India" color="#7d0431" />
                <Text style={styles.radioButtonText}>Time of India</Text>
              </View>
              <View style={styles.radioButtonRow}>
                <RadioButton value="Sakshi" color="#7d0431" />
                <Text style={styles.radioButtonText}>Sakshi</Text>
              </View>
              <View style={styles.radioButtonRow}>
                <RadioButton value="Eenadu" color="#7d0431" />
                <Text style={styles.radioButtonText}>Eenadu</Text>
              </View>
              <View style={styles.radioButtonRow}>
                <RadioButton value="Andhrajyothi" color="#7d0431" />
                <Text style={styles.radioButtonText}>Andhrajyothi</Text>
              </View>
              <View style={styles.radioButtonRow}>
                <RadioButton value="Visalandra" color="#7d0431" />
                <Text style={styles.radioButtonText}>Visalandra</Text>
              </View>
            </RadioButton.Group>
          </View>
        </View>
        {/* Bottom Modal */}
        <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible}
          onRequestClose={() => setModalVisible(false)}
        >
          <View style={styles.modalContainer}>
            <View style={styles.modalContent}>
              <View style={styles.modalColumn}>

                <Text style={styles.modalText}>Review</Text>

              </View>
              <View style={styles.modalRow}>
                <Avatar.Image
                  source={{ uri: `http://192.168.29.226:2000${filteredpaperBoy.pictures}` }}
                  style={[styles.avatarContainer, { marginRight: 10 }]}
                />
                <View style={{ flexDirection: "column" }}>
                  <Text style={styles.Name}>{filteredpaperBoy.name}</Text>
                  <Text style={styles.modalText1}>{filteredpaperBoy.phoneNumber}</Text>
                  <Text style={styles.modalText3}>{filteredpaperBoy.address}</Text>
                </View>
              </View>

              <View style={{ flexDirection: "column", marginTop: 10 }}>

                <Text style={[styles.Slot]}>Selected Timings</Text>
                <Text style={styles.modalSlot}>{radioValue}</Text>
              </View>



              <View style={styles.modalButtonContainer}>
                <TouchableOpacity
                  style={[styles.modalButton, styles.cancelButton]}
                  onPress={() => setModalVisible(false)}
                >
                  <Text style={styles.buttonText}>Cancel</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.modalButton, styles.continueButton]}
                  onPress={handleContinuePress}
                >
                  <Text style={styles.buttonText}>Confirm</Text>
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </Modal>

      </ScrollView>
      {/* <View style={{ backgroundColor: "white", height: 50, opacity: 1 }}>
        <TouchableOpacity style={styles.addbutton} onPress={handleAddPress}>
          <Text style={styles.newCardNumber1}>ADD TO HOUSEHOLD</Text>
        </TouchableOpacity>
      </View> */}
    </>
  );
};
const styles = StyleSheet.create({
